package seyyed.ali.tabatabaei.zamanakcalendar.modelimport org.junit.Testimport org.junit.Assert.assertEqualsimport seyyed.ali.tabatabaei.zamanakcalendar.model.enums.HourTypeclass ClockTest {    @Test    fun `Get hour format Hour12`(){        val clock = Clock(11 , 32 , 45)        assertEquals(11 , clock.hour)        assertEquals(11 , clock.formatHour12)        val clock2 = Clock(14 , 45 , 15)        assertEquals(14 , clock2.hour)        assertEquals(2 , clock2.formatHour12)        val clock3 = Clock(0 , 45 , 15)        assertEquals(0 , clock3.hour)        assertEquals(0 , clock3.formatHour12)        val clock4 = Clock(12 , 45 , 15)        assertEquals(12 , clock4.hour)        assertEquals(12 , clock4.formatHour12)    }    @Test    fun `Get hour type`(){        val clock = Clock(11 , 32 , 45)        assertEquals(HourType.AM , clock.getHourType())        val clock2 = Clock(14 , 45 , 15)        assertEquals(HourType.PM , clock2.getHourType())        val clock3 = Clock(0 , 45 , 15)        assertEquals(HourType.AM , clock3.getHourType())        val clock4 = Clock(12 , 45 , 15)        assertEquals(HourType.PM , clock4.getHourType())    }    @Test    fun `Get total second`(){        val clock = Clock(11 , 32 , 45)        assertEquals(41565 , clock.getTotalSecond())    }}