package seyyed.ali.tabatabaei.zamanakCalendar.core.utilsimport org.junit.Testimport org.junit.Assert.assertEqualsimport seyyed.ali.tabatabaei.zamanakCalendar.core.model.GregorianDateimport seyyed.ali.tabatabaei.zamanakCalendar.core.model.HijriDateimport seyyed.ali.tabatabaei.zamanakCalendar.core.model.JalaliDateclass DateConverterTest{    @Test    fun `convert gregorian to jalali`() {        assertEquals(JalaliDate(1404, 1, 16), DateConverter.gregorianToJalali(2025, 4, 5))        assertEquals(JalaliDate(1402, 10, 11), DateConverter.gregorianToJalali(2024, 1, 1))        assertEquals(JalaliDate(1402, 10, 10), DateConverter.gregorianToJalali(2023, 12, 31))        assertEquals(JalaliDate(1403, 1, 1), DateConverter.gregorianToJalali(2024, 3, 20))        assertEquals(JalaliDate(1399, 12, 30), DateConverter.gregorianToJalali(2021, 3, 20))        assertEquals(JalaliDate(1400, 1, 1), DateConverter.gregorianToJalali(2021, 3, 21))        assertEquals(JalaliDate(1400, 12, 29), DateConverter.gregorianToJalali(2022, 3, 20))        assertEquals(JalaliDate(1398, 12, 10), DateConverter.gregorianToJalali(2020, 2, 29))        assertEquals(JalaliDate(1402, 7, 1), DateConverter.gregorianToJalali(2023, 9, 23))        assertEquals(JalaliDate(1402, 3, 31), DateConverter.gregorianToJalali(2023, 6, 21))        assertEquals(JalaliDate(1402, 10, 1), DateConverter.gregorianToJalali(2023, 12, 22))        assertEquals(JalaliDate(1402, 6, 31), DateConverter.gregorianToJalali(2023, 9, 22))        assertEquals(JalaliDate(1278, 1, 1), DateConverter.gregorianToJalali(1899, 3, 21))        assertEquals(JalaliDate(1600, 1, 1), DateConverter.gregorianToJalali(2221, 3, 21))        assertEquals(JalaliDate(1403, 12, 30), DateConverter.gregorianToJalali(2025, 3, 20))    }    @Test    fun `convert jalali to gregorian`() {        assertEquals(GregorianDate(2025, 4, 5), DateConverter.jalaliToGregorian(1404 , 1 , 16))        assertEquals(GregorianDate(2024, 1, 1), DateConverter.jalaliToGregorian(1402 , 10 , 11))        assertEquals(GregorianDate(2023, 12, 31), DateConverter.jalaliToGregorian(1402 , 10 , 10))        assertEquals(GregorianDate(2024, 3, 20), DateConverter.jalaliToGregorian(1403 , 1 , 1))        assertEquals(GregorianDate(2021, 3, 20), DateConverter.jalaliToGregorian(1399 , 12 , 30))        assertEquals(GregorianDate(2021, 3, 21), DateConverter.jalaliToGregorian(1400 , 1 , 1))        assertEquals(GregorianDate(2022, 3, 20), DateConverter.jalaliToGregorian(1400 , 12 , 29))        assertEquals(GregorianDate(2020, 2, 29), DateConverter.jalaliToGregorian(1398 , 12 , 10))        assertEquals(GregorianDate(2023, 9, 23), DateConverter.jalaliToGregorian(1402 , 7 , 1))        assertEquals(GregorianDate(2023, 6, 21), DateConverter.jalaliToGregorian(1402 , 3 , 31))        assertEquals(GregorianDate(2023, 12, 22), DateConverter.jalaliToGregorian(1402 , 10 , 1))        assertEquals(GregorianDate(2023, 9, 22), DateConverter.jalaliToGregorian(1402 , 6 , 31))        assertEquals(GregorianDate(1899, 3, 21), DateConverter.jalaliToGregorian(1278 , 1 , 1))        assertEquals(GregorianDate(2221, 3, 21), DateConverter.jalaliToGregorian(1600 , 1 , 1))        assertEquals(GregorianDate(2025, 3, 20), DateConverter.jalaliToGregorian(1403 , 12 , 30))    }    @Test    fun `convert gregorian to hijri`() {        assertEquals(HijriDate(1446, 10, 14), DateConverter.gregorianToHijri(2025 , 4 , 13))        assertEquals(HijriDate(1445, 11, 30), DateConverter.gregorianToHijri(2024 , 6 , 7))        assertEquals(HijriDate(1445, 12, 9), DateConverter.gregorianToHijri(2024 , 6 , 16))        assertEquals(HijriDate(1445, 12, 30), DateConverter.gregorianToHijri(2024 , 7 , 7))        assertEquals(HijriDate(1446, 9, 1), DateConverter.gregorianToHijri(2025 , 3 , 1))        assertEquals(HijriDate(1446, 9, 27), DateConverter.gregorianToHijri(2025 , 3 , 27))        assertEquals(HijriDate(1446, 10, 2), DateConverter.gregorianToHijri(2025 , 4 , 1))        assertEquals(HijriDate(1444, 8, 15), DateConverter.gregorianToHijri(2023 , 3 , 8))        assertEquals(HijriDate(1399, 12, 29), DateConverter.gregorianToHijri(1979 , 11 , 20))    }    @Test    fun `convert hijri to gregorian`() {        assertEquals(GregorianDate(2025, 4, 13), DateConverter.hijriToGregorian(1446, 10, 14))        assertEquals(GregorianDate(2024, 6, 7), DateConverter.hijriToGregorian(1445, 11, 30))        assertEquals(GregorianDate(2024, 6, 16), DateConverter.hijriToGregorian(1445, 12, 9))        assertEquals(GregorianDate(2024, 7, 7), DateConverter.hijriToGregorian(1445, 12, 30))        assertEquals(GregorianDate(2025, 3, 1), DateConverter.hijriToGregorian(1446, 9, 1))        assertEquals(GregorianDate(2025, 3, 27), DateConverter.hijriToGregorian(1446, 9, 27))        assertEquals(GregorianDate(2025, 4, 1), DateConverter.hijriToGregorian(1446, 10, 2))        assertEquals(GregorianDate(2023, 3, 8), DateConverter.hijriToGregorian(1444, 8, 15))        assertEquals(GregorianDate(1979, 11, 20), DateConverter.hijriToGregorian(1399, 12, 29))    }}